import React from 'react';
import {validate} from './validators';
import { useState,useRef } from 'react';

const INPUT_STATES = {
  UNTOUCHED: 'UNTOUCHED',
  VALID: 'VALID',
  INVALID: 'INVALID'
};

const Input = props => {
const[value,setValue]=useState('')
const[status,setStatus]=useState('')

 const{type,label,id,validators,errorText}=props
 validate(value,validators)

const focusHandler=()=>{
if(validate(value,validators)===false){
  setStatus(INPUT_STATES.INVALID) 
 }
 if(validate(value,validators)===true){
  setStatus(INPUT_STATES.VALID) 
 }


}



   return (
     <div className={`form-input ${status===INPUT_STATES.INVALID&&'form-input--invalid'}`} data-testid="form-input">
       <label>{label}</label>
       <input onFocus={focusHandler} value={value} type={type} id={id} onChange={(e)=>setValue(e.target.value)} />

       {status===INPUT_STATES.INVALID&&(




      <p>{errorText}</p>




       )}

     </div>
   )
};

export default Input;
